mapper.select:

SELECT * FROM user_crew LEFT OUTER JOIN crew
ON user_crew.crewId = crew.crewId
WHERE userId = #{userId}

mapper.insert:
INSERT INTO user_crew (userId, crewId)
SELECT #{userId}, #{crewId}
WHERE NOT EXISTS (
    SELECT userId
    FROM user_crew
    WHERE userId = #{userId} AND crewId = #{crewId}
)
